settings.custom_liquid:
{% assign allBlogs = blogs %}

<div class="links_blog">
  <ul>
    <li{% if request.path == '/blogs/all' %} class="active"{% endif %}><a href="/blogs/all">ALL</a></li>
    <li{% if request.path == '/blogs/skin-care' %} class="active"{% endif %}><a href="/blogs/skin-care">Skin Care</a></li>
    <li{% if request.path == '/blogs/news' %} class="active"{% endif %}><a href="/blogs/news">News</a></li>
    <!-- Add more links for other blog sections as needed -->
  </ul>
</div>


<style>
.links_blog {
    width: 1200px;
    margin: 0 auto;
    background: #FDF6F2;
    padding: 20px 0;
    text-align: center;
}

.links_blog ul {
    list-style: none;
    display: flex;
    justify-content: space-evenly;
    align-items: center;
}

.links_blog ul li {
    display: inline;
}

.links_blog ul li.active {
    color: #fff;
    background: #FE619E;
    padding: 14px 20px;
    border-radius: 5px; /* Add border-radius for a rounded look */
}

.links_blog ul li.active a {
    color: #fff;
}

.links_blog ul li a {
    font-size: 18px;
    font-weight: 600;
    color: #000;
    font-family: Futura_Book_font !important;
    text-decoration: none; /* Remove underline from links */
    padding: 12px 17px; /* Adjust padding for a more balanced look */
    transition: color 0.3s, background 0.3s; /* Add a smooth transition effect */
}

.links_blog ul li a:hover {
    color: #fff;
    background: #FE619E;
    border-radius: 5px;
}

.blog_main_title{
display:none;
}

@media only screen and (max-width: 600px) {
  .links_blog {
    width: auto;
}
}


</style>
